import './App.css';
import { Editor, Sidebar } from './components';
import { useState } from 'react';
import uuid from 'react-uuid'

const App = () => {

  const [notes, setNotes] = useState([]);

  const onAddNote = () => {
    const newNote = {
      id: uuid(),
      title: "Untitled Note",
      body: "",
      lastModified: Date.now() ,
    };

    setNotes([newNote, ...notes]); //The ... means that we will take everything in the existing notes array and put it into this new array
  }

  return (
    <div className='App'>
      <Sidebar prop_Notes={notes} prop_OnAddNote={onAddNote}/>
      <Editor/>
    </div>
  )
}

export default App;
