import { useEditor, EditorContent } from '@tiptap/react'
import StarterKit from '@tiptap/starter-kit'
import TextStyle from '@tiptap/extension-text-style'
import { Color } from '@tiptap/extension-color'

import UndoIcon from '@mui/icons-material/Undo';
import RedoIcon from '@mui/icons-material/Redo';
import FormatListBulletedIcon from '@mui/icons-material/FormatListBulleted';
import FormatListNumberedIcon from '@mui/icons-material/FormatListNumbered';

const Editor = () => {
  const editor = useEditor({
    content: '<p></p>',
    extensions: [
      StarterKit, TextStyle, Color,
    ],
  })

  if (!editor) {
    return null
  }

  return (
    <div>
      <EditorNavbar>editor</EditorNavbar>
      <div>
        <EditorContent editor={editor} />
      </div>
    </div>
  )
}

function EditorNavbar(){
  return(
    <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', paddingBottom: '5px', borderBottom: '2px solid black'}}>
      <div className='undo-redo' style={{marginRight: '10px'}}>
        <button onClick={() => props.editor.chain().focus().undo().run()} disabled={!props.editor.can().undo()} style={{border: 0, backgroundColor: 'white'}}>
          <UndoIcon/>
        </button>
        <button onClick={() => props.editor.chain().focus().redo().run()} disabled={!props.editor.can().redo()} style={{border: 0, backgroundColor: 'white'}}>
          <RedoIcon/>
        </button>
      </div>

      <div className='font-bold' style={{marginRight: '10px'}}>
        <button
        onClick={() => props.editor.chain().focus().toggleBold().run()}
        disabled={ !props.editor.can() .chain() .focus() .toggleBold() .run() }
        className={props.editor.isActive('bold') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white', fontSize: '20px'}}
        >
          <strong>B</strong>
        </button>
      </div>
      
      <div className='font-italic' style={{marginRight: '10px'}}>
        <button
        onClick={() => props.editor.chain().focus().toggleItalic().run()}
        disabled={!props.editor.can() .chain() .focus() .toggleItalic() .run()}
        className={props.editor.isActive('italic') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white', fontSize: '20px'}}
        >
          <strong><i>I</i></strong>
        </button>
      </div>

      <div className='font-color' style={{marginRight: '10px'}}>
        <input 
          type="color" 
          style={{marginRight: '5px'}} 
          onInput={event => props.editor.chain().focus().setColor(event.target.value).run()} 
          value={props.editor.getAttributes('textStyle').color}
        />
        <label>
          Font Color
        </label>
      </div>

      <div className='bullet-list' style={{marginRight: '10px', marginTop: '4px'}}>
        <button
        onClick={() => props.editor.chain().focus().toggleBulletList().run()}
        className={props.editor.isActive('bulletList') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white'}}
        >
          <FormatListBulletedIcon/>
        </button>      
      </div>

      <div className='ordered-list' style={{marginRight: '10px', marginTop: '4px'}}>
        <button
        onClick={() => props.editor.chain().focus().toggleOrderedList().run()}
        className={props.editor.isActive('orderedList') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white'}}
        >
          <FormatListNumberedIcon/>
        </button>      
      </div>
    </div>
  );
}

export default Editor
