import { useState } from 'react'

const Editor = ( {prop_GetActiveNote, prop_OnUpdateNote}) => {
  const onEditField = (key, value) => {
    prop_OnUpdateNote({
      ...prop_GetActiveNote,
      id: prop_GetActiveNote.id,
      [key]: value,
      lastModified: Date.now(),
    })
  };

  const generatePDF = () => {
    let doc = new jsPDF("p", "pt", "a4");
    doc.html(document.querySelector("#content"), {
      callback: function(pdf){
        pdf.save("mypdf.pdf");
      } 
    });
  };



  if(!prop_GetActiveNote) return <div className='no-active-note'>No selected note</div>
  

  return (
    <div className='hero-container'>
      <div className='main-note-editor-container'>     
        <input 
          id='note-title' 
          type='text' 
          onChange={(event) => onEditField("title", event.target.value)} 
          autoFocus 
          value={prop_GetActiveNote.title} 
        />

        <textarea
          id="body"
          value={prop_GetActiveNote.body}
          onChange={(event) => onEditField("body", event.target.value)}
        />
      </div>
    </div>


      
    
    

    
  )
}

export default Editor
