import UndoIcon from '@mui/icons-material/Undo';
import RedoIcon from '@mui/icons-material/Redo';
import FormatListBulletedIcon from '@mui/icons-material/FormatListBulleted';
import FormatListNumberedIcon from '@mui/icons-material/FormatListNumbered';


const EditorNavbar = (props) => {
  return (
    <div style={{display: 'flex', justifyContent: 'center', alignItems: 'center', paddingBottom: '5px', borderBottom: '2px solid black'}}>
      <div className='undo-redo' style={{marginRight: '10px'}}>
        <button onClick={() => props.children.chain().focus().undo().run()} disabled={!props.children.can().undo()} style={{border: 0, backgroundColor: 'white'}}>
          <UndoIcon/>
        </button>
        <button onClick={() => props.children.chain().focus().redo().run()} disabled={!props.children.can().redo()} style={{border: 0, backgroundColor: 'white'}}>
          <RedoIcon/>
        </button>
      </div>

      <div className='font-bold' style={{marginRight: '10px'}}>
        <button
        onClick={() => props.children.chain().focus().toggleBold().run()}
        disabled={ !props.children.can() .chain() .focus() .toggleBold() .run() }
        className={props.children.isActive('bold') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white', fontSize: '20px'}}
        >
          <strong>B</strong>
        </button>
      </div>
      
      <div className='font-italic' style={{marginRight: '10px'}}>
        <button
        onClick={() => props.children.chain().focus().toggleItalic().run()}
        disabled={!props.children.can() .chain() .focus() .toggleItalic() .run()}
        className={props.children.isActive('italic') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white', fontSize: '20px'}}
        >
          <strong><i>I</i></strong>
        </button>
      </div>

      <div className='font-color' style={{marginRight: '10px'}}>
        <input 
          type="color" 
          style={{marginRight: '5px'}} 
          onInput={event => props.children.chain().focus().setColor(event.target.value).run()} 
          value={props.children.getAttributes('textStyle').color}
        />
        <label>
          Font Color
        </label>
      </div>

      <div className='bullet-list' style={{marginRight: '10px', marginTop: '4px'}}>
        <button
        onClick={() => props.children.chain().focus().toggleBulletList().run()}
        className={props.children.isActive('bulletList') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white'}}
        >
          <FormatListBulletedIcon/>
        </button>      
      </div>

      <div className='ordered-list' style={{marginRight: '10px', marginTop: '4px'}}>
        <button
        onClick={() => props.children.chain().focus().toggleOrderedList().run()}
        className={props.children.isActive('orderedList') ? 'is-active' : ''}
        style={{border: 0, backgroundColor: 'white'}}
        >
          <FormatListNumberedIcon/>
        </button>      
      </div>
  </div>
  )
}

export default props.childrenNavbar
