import './App.css';
import { Editor, Sidebar, Toolbar } from './components';
import { useState } from 'react';
import uuid from 'react-uuid'

import { useEditor, EditorContent } from '@tiptap/react'
import StarterKit from '@tiptap/starter-kit'
import TextStyle from '@tiptap/extension-text-style'
import { Color } from '@tiptap/extension-color'
import FontFamily from '@tiptap/extension-font-family'
import Link from '@tiptap/extension-link'
import Highlight from '@tiptap/extension-highlight'

import CharacterCount from '@tiptap/extension-character-count'
import Underline from '@tiptap/extension-underline'

import { FontSize } from './Extentions/FontSize'

const App = () => {

  const [notes, setNotes] = useState([]);
  const [activeNote, setActiveNote] = useState(false);

  const onAddNote = () => {
    const newNote = {
      id: uuid(),
      title: "Untitled Note",
      body: "",
      lastModified: Date.now() ,
    };

    setNotes([newNote, ...notes]); //The ... means that we will take everything in the existing notes array and put it into this new array
  }

  const getActiveNote = () => {
    return notes.find((note) => note.id === activeNote)
  }

  const onDeleteNote = (deletedNoteID) => {
    setNotes(notes.filter((note) => note.id != deletedNoteID)); //Will print false if it matches with the to be deleted note id and then remove it
  }

  const onUpdateNote = (updateNote) => {
    const updateNotesArray = notes.map((note) => {
      if(note.id === activeNote){
        return updateNote;
      }

      return note;
    });

    setNotes(updateNotesArray);
  }

  const editor = useEditor({
    content: `<p></p>`,
    extensions: [
      StarterKit, TextStyle, Color, CharacterCount, FontSize, 
      Underline, FontFamily,
    ],
  })

  

  return (
    <div className='App'>
      <Sidebar prop_Editor={editor} prop_Notes={notes} prop_OnAddNote={onAddNote} prop_OnDeleteNote={onDeleteNote} prop_ActiveNote={activeNote} prop_SetActiveNote={setActiveNote}/>
      <Editor prop_GetActiveNote={getActiveNote()} prop_OnUpdateNote={onUpdateNote}/>
    </div>
  )
}

export default App;
