import { useState } from 'react'
import Toolbar from './ToolBar';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';

const Editor = ( {prop_GetActiveNote, prop_OnUpdateNote}) => {
 
  const onEditField = (key, value) => {
    
    prop_OnUpdateNote({
      ...prop_GetActiveNote,
      id: prop_GetActiveNote.id,
      [key]: value,
      lastModified: Date.now(),
    })
  };

  var contenteditable = document.querySelector('[contenteditable]'),
    text = contenteditable.textContent;

  document.getElementById('text-input').addEventListener('input', function() {
    onEditField("body", text)
  });

  if(!prop_GetActiveNote) return <div className='no-active-note'>No selected note</div>  

  return (
    <div className='hero-container'>
      <div className='main-note-editor-container'>     
        <input 
          id='note-title' 
          type='text' 
          onChange={(event) => onEditField("title", event.target.value)} 
          autoFocus 
          value={prop_GetActiveNote.title} 
        />


        <Toolbar/>

        <textarea
          id="body"
          value={prop_GetActiveNote.body}
          onChange={(e) => onEditField("body", e.target.value)}
        />

        <div id="text-input" onChange={(e) => onEditField("body", e.target.value)} className='test' contentEditable="true"></div>
        
      </div>
    </div>


      
    
    

    
  )
}

export default Editor
